mapper.select:
SELECT  voucher_id,
        code,
        type,
        value,
        min_spend,
        start_date,
        end_date,
        usage_limit,
        create_at,
        update_at
FROM voucher



        AND voucher_id = #{voucherId}



        AND LOWER(code) LIKE CONCAT('%', LOWER(#{code}), '%')



        AND type = #{type}



        AND value = #{discountValue}



        AND min_spend = #{minSpend}



        AND start_date = #{startDate}



        AND end_date = #{endDate}



        AND usage_limit = #{usageLimit}


ORDER BY voucher_id DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(voucher_id)
FROM voucher



        AND voucher_id = #{voucherId}



        AND LOWER(code) LIKE CONCAT('%', LOWER(#{code}), '%')



        AND type = #{type}



        AND value = #{discountValue}



        AND min_spend = #{minSpend}



        AND start_date = #{startDate}



        AND end_date = #{endDate}



        AND usage_limit = #{usageLimit}
